Templates.template:
fireColumnMenuOpen(
	{
		menu: ${menu} // sap.ui.unified.Menu
	}
)

fireBeforeOpenContextMenu(
	{
		rowIndex: ${rowIndex}, // int
		columnIndex: ${columnIndex}, // int
		contextMenu: ${contextMenu} // sap.ui.core.IContextMenu
	}
)

fireCellClick(
	{
		cellControl: ${cellControl}, // sap.ui.core.Control
		cellDomRef: ${cellDomRef}, // Object
		rowIndex: ${rowIndex}, // int
		columnIndex: ${columnIndex}, // int
		columnId: "${columnId}", // string
		rowBindingContext: ${rowBindingContext} // sap.ui.model.Context
	}
)

fireCellContextmenu(
	{
		cellControl: ${cellControl}, // sap.ui.core.Control
		cellDomRef: ${cellDomRef}, // Object
		rowIndex: ${rowIndex}, // int
		columnIndex: ${columnIndex}, // int
		columnId: "${columnId}", // string
		rowBindingContext: ${rowBindingContext} // sap.ui.model.Context
	}
)

fireColumnFreeze(
	{
		column: ${column} // sap.ui.table.Column
	}
)

fireColumnMove(
	{
		column: ${column}, // sap.ui.table.Column
		newPos: ${newPos} // int
	}
)

fireColumnResize(
	{
		column: ${column}, // sap.ui.table.Column
		width: ${width} // sap.ui.core.CSSSize
	}
)

fireColumnSelect(
	{
		column: ${column} // sap.ui.table.Column
	}
)

fireColumnVisibility(
	{
		column: ${column}, // sap.ui.table.Column
		visible: ${bVisible} // boolean
	}
)

fireFilter(
	{
		column: ${column}, // sap.ui.table.Column
		value: "${value}" // string
	}
)

fireGroup(
	{
		column: ${column} // sap.ui.table.Column
	}
)

fireRowSelectionChange(
	{
		rowIndex: ${rowIndex}, // int
		rowContext: ${rowContext}, // object
		rowIndices: [${rowIndices}], // [object Object][]
		selectAll: ${bSelectAll}, // boolean
		userInteraction: ${bUserInteraction} // boolean
	}
)

fireSort(
	{
		column: ${column}, // sap.ui.table.Column
		sortOrder: ${sortOrder}, // sap.ui.table.SortOrder
		columnAdded: ${bColumnAdded} // boolean
	}
)

fireToggleOpenState(
	{
		rowIndex: ${rowIndex}, // int
		rowContext: ${rowContext}, // object
		expanded: ${bExpanded} // boolean
	}
)
