    _cloneObj:function(ele){
        var tmp;
        if(ele instanceof Array){
            tmp=[];
            for(var i=0;i<ele.length;i++){
                tmp[i]=this._cloneObj(ele[i]);
            }
        }else if(ele instanceof Object){
            tmp={};
            for(var each in ele){
                if(ele.hasOwnProperty(each)){
                    tmp[each]=this._cloneObj(ele[each]);     
                }
            }
        }else{
            tmp=ele;
        }
        return tmp;
    },
