					var kpiPayload = {payload:indicatorPayload};
					kpiPayload.keys = {};
					kpiPayload.keys.ID = indicatorPayload.ID;
					kpiPayload.keys.IS_ACTIVE = indicatorPayload.IS_ACTIVE;
